 },\n    \"bizberg::body_background_image\": {\n        \"value\": {\n            \"background-color\": \"rgba(255,255,255,0)\",\n            \"background-image\": \"\",\n            \"background-repeat\": \"repeat\",\n            \"background-position\": \"center center\",\n            \"background-size\": \"cover\",\n            \"background-attachment\": \"scroll\"\n        },\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-04-27 10:48:06\"\n    },\n    \"bizberg::custom_search_page_image\": {\n        \"value\": {\n            \"background-color\": \"rgba(20,20,20,.8)\",\n            \"background-image\": \"\",\n            \"background-repeat\": \"repeat\",\n            \"background-position\": \"center center\",\n            \"background-size\": \"cover\",\n            \"background-attachment\": \"scroll\"\n        },\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-04-27 10:48:06\"\n    },\n    \"bizberg::custom_archive_page_image\": {\n        \"value\": {\n            \"background-color\": \"rgba(20,20,20,.8)\",\n            \"background-image\": \"\",\n            \"background-repeat\": \"repeat\",\n            \"background-position\": \"center center\",\n            \"background-size\": \"cover\",\n            \"background-attachment\": \"scroll\"\n        },\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-04-27 10:48:06\"\n    },\n    \"bizberg::custom_single_page_image\": {\n        \"value\": {\n            \"background-color\": \"rgba(20,20,20,.8)\",\n            \"background-image\": \"\",\n            \"background-repeat\": \"repeat\",\n            \"background-position\": \"center center\",\n            \"background-size\": \"cover\",\n            \"background-attachment\": \"scroll\"\n        },\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-04-27 10: